import React from "react";
import {useNavigate} from 'react-router-dom'

const AccommodationEdit = (props) => {
    const navigate = useNavigate();
    const [formData, updateFormData] = React.useState({
        name : "",
        category : "ROOM",
        host : 1,
        numRooms : 0,
        available : true
    })

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name] : e.target.value.trim()
        })
    }

    const onFormSubmit = (e) => {
        e.preventDefault();
        const name = formData.name;
        const category = formData.category;
        const host = formData.host;
        const numRooms = formData.numRooms;
        const available = formData.available;


        props.onEditAcc(id, name, category, host, numRooms, available);
        navigate("/accommodations")
    }

    return (
        <div className="row mt-5">
            <div className="col-md-5">
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Accommodation name</label>
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder={props.accommodation.name}
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Category</label>
                        <select name="category" className="form-control" onChange={handleChange}>
                            {props.categories.map((term) =>
                                <option value={term}>{term}</option>
                            )}
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Host</label>
                        <select name="host" className="form-control" onChange={handleChange}>
                            {props.hosts.map((term) => {
                                    if (props.accommodation.category !== undefined && props.accommodation.category === term)
                                        return <option value={term.id}>{term.name}</option>

                                }
                            )}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="numRooms">NumRooms</label>
                        <input type="number"
                               className="form-control"
                               id="numRooms"
                               name="numRooms"
                               placeholder={props.accommodation.numRooms}
                               required
                               onChange={handleChange}
                        />
                    </div>
                    <button id="submit" type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>

    )
}
export default AccommodationEdit;
