import logo from '../../logo.svg';
import './App.css';
import React from "react";
import {BrowserRouter as Router, Route} from "react-router-dom";
import Hosts from "../Hosts/hosts";
import BookingService from "../../repository/bookingRepository";
import Accommodations from "../Accommodations/AccommodationList/accommodations";
import Countries from "../Countries/countries";
import data from "bootstrap/js/src/dom/data";

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            hosts : [],
            accommodations : [],
            countries : []

        }
    }

    render() {
        return (
          <Router>
              <main>
                 <div className="container">
                     <Route path={"/hosts"} exact render={() => <Hosts hosts={this.state.hosts}/>}/>
                     <Route path={"/accommodations"} exact render={() => <Accommodations accommodations={this.state.accommodations}/>}/>
                     <Route path={"/countries"} exact render={() => <Countries countries={this.state.countries}/>}/>
                 </div>
              </main>
          </Router>
        );
    }

    loadHosts = () => {
        BookingService.fetchHosts()
            .then((data) => {
                this.setState({
                    hosts: data.data
                })
            })
    }

    loadAccommodations = () => {
        BookingService.fetchAccommodations()
            .then((data) => {
                this.setState({
                    accommodations: data.data
                })
            })
    }
    loadCountries = () => {
        BookingService.fetchCountries()
            .then((data) => {
                this.setState({
                    countries: data.data
                })
            })
    }

    componentDidMount() {
        this.loadHosts();
        this.loadCountries();
        this.loadAccommodations();
    }
}

export default App;
